package com.library.entity;


import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.Table;

@Entity // Marks the class as a JPA entity, indicating that it is a persistent object
		// that can be stored in a database.
@Table(name = "LIB_IMAGE") // Specifies the name of the database table to which this entity is mapped.

public class Image {
	// Marks the field as the primary key of the entity.
	@Id 
	//Specifies that the primary key value is generated by the database (auto-increment).
	@GeneratedValue(strategy = GenerationType.IDENTITY) 
	private Long id; 

	private String name; 
 
	// Binary data representing the image
	private byte[] imageData; 


	public Long getId() {
		return id;
	}

	public void setId(Long id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public byte[] getImageData() {
		return imageData;
	}

	public void setImageData(byte[] imageData) {
		this.imageData = imageData;
	}

}